mapper.select:
SELECT
    deal_store_id
FROM
    deal_user_store

    deal_user_id = #{dealUserId}
    AND examine = #{examine}

mapper.update:
UPDATE
    deal_user AS deal_user
SET
    deal_user.type = #{type}
WHERE
    deal_user_id = #{dealUserId}

UPDATE
    deal_assess
SET
    sell_status = #{status}
WHERE
    deal_assess_id = #{dealAssessId}
