mapper.select:
SELECT deal_user_store.deal_store_id, deal_user.deal_user_id, deal_user.deal_user_name, deal_user.phone, deal_user_store.deal_store_name, deal_user_store.deal_user_job, deal_user_store.sys_user_id, deal_user_store.sys_user_name, deal_user_store.examine, deal_user_store.examine_user_id, deal_user_store.examine_time FROM deal_user_store AS deal_user_store JOIN deal_user AS deal_user ON deal_user_store.deal_user_id = deal_user.deal_user_id
deal_user_store.deal_user_id = #{dealUserId}
deal_user.phone = #{dealUserPhone}
ORDER BY apply_time DESC

mapper.update:
UPDATE
    deal_user_store
SET
    examine = #{waste}
WHERE
    deal_user_id = #{dealUserId} AND examine = #{success} AND deal_store_id != #{dealStoreId}
