mapper.select:
SELECT
    COUNT(1)
FROM
    sys_user


        phone = #{phone}


        AND user_id != #{userId}

SELECT sys_user.user_id, sys_user.user_name, sys_user.phone, sys_user.status, sys_user.login_ip, sys_user.login_time, sys_dept.dept_id, sys_dept.dept_name FROM sys_user AS sys_user LEFT JOIN sys_user_dept AS sys_user_dept ON sys_user.user_id = sys_user_dept.user_id LEFT JOIN sys_dept AS sys_dept ON sys_user_dept.dept_id = sys_dept.dept_id
sys_user.user_name LIKE "%"#{userName}"%"
AND sys_user.phone = #{phone}
AND sys_user.status = #{status}
AND sys_dept.dept_id IN
#{deptId}
ORDER BY sys_user.user_id ASC
